'use strict'

class BasePromises {
  async op (f) {
    return new Promise((resolve, reject) => {
      setImmediate(() => {
        try {
          f((err, v) => {
            if (err) {
              setImmediate(() => { reject(err) })
            } else {
              setImmediate(() => { resolve(v) })
            }
          })
        } catch (e) {
          setImmediate(() => { reject(e) })
        }
      })
    })
  }
}
